<script>
import {nextTick, ref, watch} from "vue";
import {getComment} from "@/api/recommond";
import {useRoute} from "vue-router";
import {formatDate} from "@vueuse/shared/index";
import router from "@/router";


export default {
  name: 'XComments',
  props:[],
  setup(props,{emit}){
    const route = useRoute()
    const offset= ref(0)
    const limit= ref(50)
    const timestamp= Date.parse(new Date());
    const commentList = ref({})
    const total = ref(0)
    const totals = ref(0)
    getComment(route.params.id,offset.value,limit.value,timestamp).then(res=>{
      commentList.value = res.data
      total.value=commentList.value.total
      totals.value = Math.ceil(total.value/limit.value)
      // alert(total.value)
    })
    const zz=()=>{
      getComment(route.params.id,offset.value,limit.value,timestamp).then(res=>{
        commentList.value = res.data
        total.value=commentList.value.total
        totals.value = Math.ceil(total.value/limit.value)
        // alert(total.value)
      })
    }
    watch(total,()=>{
      emit('getCommentTotal',total.value)
    })
    //改变页面
    const currentchange = (val)=>{
      offset.value = (val-1)*50
      zz()
    }
    //跳转到用户页面
    const toUserpage = (item) =>{
      const uid = item.user.userId
      router.push({name:'userpage',params: {uid}})
    }
    return{
      commentList,
      formatDate,
      currentchange,
      totals,
      toUserpage
    }
  }
}
</script>

